-- ========================================
-- 创建foods表
-- ========================================

CREATE TABLE IF NOT EXISTS foods (
    food_id INT PRIMARY KEY COMMENT '食物ID',
    food_name VARCHAR(100) NOT NULL COMMENT '食物名称',
    calory_per_100g DECIMAL(10,2) COMMENT '每100克卡路里',
    img_url VARCHAR(500) COMMENT '食物图片URL',
    category VARCHAR(50) COMMENT '食物类别',
    unit VARCHAR(20) COMMENT '基础单位',
    refer_unit VARCHAR(50) COMMENT '参考单位',
    unit_count DECIMAL(10,2) COMMENT '单位数量（克）',
    unit_weight DECIMAL(10,2) COMMENT '单位重量',
    created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
    updated_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT '更新时间',
    INDEX idx_food_name (food_name),
    INDEX idx_category (category)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci COMMENT='食物信息表';

