-- ========================================

-- 创建视图，方便查询daily_foods时关联foods表的信息
CREATE OR REPLACE VIEW daily_foods_detail AS
SELECT
    df.id,
    df.daily_meal_id,
    df.meal_type,
    df.meal_name,
    df.meal_calories,
    df.food_name,
    df.food_count,
    df.unit,
    df.display_order,
    df.food_id,
    f.food_name AS food_name_full,
    f.calory_per_100g,
    f.img_url AS food_img_url,
    f.category,
    -- 计算单个食物的卡路里
    CASE
        WHEN df.unit COLLATE utf8mb4_unicode_ci = '克' COLLATE utf8mb4_unicode_ci 
             OR df.unit COLLATE utf8mb4_unicode_ci = '毫升' COLLATE utf8mb4_unicode_ci THEN
            (df.food_count / 100.0) * f.calory_per_100g
        WHEN df.unit COLLATE utf8mb4_unicode_ci = f.refer_unit COLLATE utf8mb4_unicode_ci 
             AND f.unit_count IS NOT NULL THEN
            (df.food_count * f.unit_count / 100.0) * f.calory_per_100g
        WHEN f.unit_weight IS NOT NULL THEN
            (df.food_count * f.unit_weight / 100.0) * f.calory_per_100g
        ELSE NULL
    END AS food_calories
FROM daily_foods df
LEFT JOIN foods f ON df.food_id = f.food_id;